import { Component, Fragment } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Modal,
  ModalTitle,
  ModalBody,
  ModalFooter,
  Form,
} from "react-bootstrap";
import NavBar from "./NavBar";
import axios from "axios";

class UserBlogs extends Component {
  state = {
    showBlogModal: false,
    userBlogData: [],
  };

  openBlogModal = () => {
    this.setState({
      showBlogModal: true,
    });
  };

  closeBlogModal = () => {
    this.setState({
      showBlogModal: false,
    });
  };

  addBlog = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  postBlogs = () => {
    this.setState(
      {
        userBlogData: [
          ...this.state.userBlogData,
          {
            blogTitle: this.state.blogTitle,
            blogSubTitle: this.state.blogSubTitle,
            blogDescription: this.state.blogDescription,
            blogPhoto: this.state.blogPhoto,
          },
        ],
      },
      async () => {
        await axios.post(
          "http://localhost:9999/api/userBlogs",
          this.state.userBlogData
        );
      }
    );
  };

  render() {
    if (localStorage.getItem("userPersonalData") === "") {
      this.props.history.push("/");
      window.location.reload();
    }
    return (
      <Fragment>
        <NavBar />
        <Container>
          <Row className="user-blog-profile py-5">
            <Col
              xs={12}
              className="d-flex flex-column align-items-center justify-content-around"
            >
              {localStorage.getItem("userPersonalData") ? (
                <>
                  <span className="user-avatar-large text-secondary">
                    {
                      JSON.parse(localStorage.getItem("userPersonalData"))
                        .username[0]
                    }
                  </span>
                  <h1 className="text-dark py-3" id="username">
                    {
                      JSON.parse(localStorage.getItem("userPersonalData"))
                        .username
                    }
                  </h1>
                  <h6 className="text-secondary" id="userEmailAddress">
                    {
                      JSON.parse(localStorage.getItem("userPersonalData"))
                        .userEmailAddress
                    }
                  </h6>
                </>
              ) : (
                <h1>No data found </h1>
              )}
              <Button
                variant="outline-secondary"
                className="my-3 px-5"
                onClick={() => {
                  localStorage.setItem("userPersonalData", "");
                  this.props.history.push("/");
                }}
              >
                Sign Out
              </Button>
            </Col>
          </Row>
          <Row>
            <Col xs={12} md={6}>
              <h3 className="py-3 text-sm-center d-flex align-items-center justify-content-start justify-content-xs-center">
                {JSON.parse(localStorage.getItem("userPersonalData")).username +
                  "'s Blogs"}
              </h3>
            </Col>
            <Col
              xs={12}
              md={6}
              className="d-flex align-items-center justify-content-center justify-content-md-end"
            >
              <Button onClick={this.openBlogModal}>Add a Blog</Button>
            </Col>
          </Row>
        </Container>
        {/* modal for user to write write blogs */}
        <Modal
          show={this.state.showBlogModal}
          size="lg"
          onHide={this.closeBlogModal}
          centered
        >
          <Modal.Header closeButton>
            <ModalTitle>Write your own blog</ModalTitle>
          </Modal.Header>
          <ModalBody>
            <Form>
              <Form.Group>
                <Form.Label>Blog Title</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter Blog Title"
                  name="blogTitle"
                  onChange={this.addBlog}
                  required
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Blog Sub Title</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Enter Blog Sub Title"
                  name="blogSubTitle"
                  onChange={this.addBlog}
                  required
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Blog description</Form.Label>
                <Form.Control
                  as="textarea"
                  rows={5}
                  placeholder="Enter Blog Description"
                  name="blogDescription"
                  onChange={this.addBlog}
                  required
                />
              </Form.Group>
              <Form.Group>
                <Form.File
                  label="Chose Blog Photo"
                  onChange={this.addBlog}
                  name="blogPhoto"
                  required
                />
              </Form.Group>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button
              variant="primary"
              type="button"
              onClick={() => {
                this.closeBlogModal();
                this.postBlogs(
                  JSON.parse(localStorage.getItem("userPersonalData")).username
                );
              }}
            >
              Add Blog
            </Button>
          </ModalFooter>
        </Modal>
        {/* <Container fluid>

        </Container> */}
      </Fragment>
    );
  }
}

export default UserBlogs;

