import { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Modal,
  ModalTitle,
  ModalBody,
  ModalFooter,
  Form,
} from "react-bootstrap";
import NavBar from "./NavBar";

const UserBlogs = ({ history }) => {
  useEffect(() => {
    if (localStorage.getItem("userPersonalData") === "") {
      history.push("/");
    }
  }, [history]);

  const [blogModal, showBlogModal] = useState(false);

  const openBlogModal = () => {
    showBlogModal(true);
  };

  const closeBlogModal = () => {
    showBlogModal(false);
  };

  return (
    <>
      <NavBar />
      <Container>
        <Row className="user-blog-profile py-5">
          <Col
            xs={12}
            className="d-flex flex-column align-items-center justify-content-around"
          >
            {localStorage.getItem("userPersonalData") ? (
              <>
                <span className="user-avatar-large text-secondary">
                  {
                    JSON.parse(localStorage.getItem("userPersonalData"))
                      .username[0]
                  }
                </span>
                <h1 className="text-dark py-3" id="username">
                  {
                    JSON.parse(localStorage.getItem("userPersonalData"))
                      .username
                  }
                </h1>
                <h6 className="text-secondary" id="userEmailAddress">
                  {
                    JSON.parse(localStorage.getItem("userPersonalData"))
                      .userEmailAddress
                  }
                </h6>
              </>
            ) : (
              <h1>No data found </h1>
            )}
            <Button
              variant="outline-secondary"
              className="my-3 px-5"
              onClick={() => {
                localStorage.setItem("userPersonalData", "");
                history.push("/");
              }}
            >
              Sign Out
            </Button>
          </Col>
        </Row>
        <Row>
          <Col xs={12} md={3}>
            <h3 className="py-3 pr-lg-5 text-center">
              {localStorage.getItem("userPersonalData")
                ? JSON.parse(localStorage.getItem("userPersonalData"))
                    .username + "'s Blogs"
                : "No User Found"}
            </h3>
          </Col>
          <Col
            xs={12}
            md={9}
            className="d-flex align-items-center justify-content-center justify-content-lg-end"
          >
            <Button onClick={openBlogModal}>Add a Blog</Button>
          </Col>
        </Row>
      </Container>
      {/* modal for user to write write blogs */}
      <Modal show={blogModal} size="lg" centered onHide={closeBlogModal}>
        <Modal.Header closeButton>
          <ModalTitle>Write your own blog</ModalTitle>
        </Modal.Header>
        <ModalBody>
          <Form>
            <Form.Group>
              <Form.Label>Blog Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Blog Title"
                required
                // onChange={handleUsername}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Blog Sub Title</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter Blog Sub Title"
                // onChange={handleUserEmailAddress}
                required
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Blog description</Form.Label>
              <Form.Control
                as="textarea"
                rows={5}
                placeholder="Enter Blog Description"
                // onChange={handleUserPassword}
                required
              />
            </Form.Group>
            <Form.Group>
              <Form.File label="Chose Blog Photo" required />
            </Form.Group>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button
            variant="primary"
            type="button"
            onClick={() => {
              closeBlogModal();
            }}
          >
            Add Blog
          </Button>
        </ModalFooter>
      </Modal>
      {/* <Container fluid>

      </Container> */}
    </>
  );
};

export default UserBlogs;
