import { useEffect } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";

const UserBlogs = ({ history }) => {
  useEffect(() => {
    if (localStorage.getItem("userPersonalData")) {
      console.log("worked");
    } else {
      history.push("/");
    }
  }, [history]);
  return (
    <Container>
      <Row>
        <Col
          xs={12}
          className="d-flex flex-column align-items-center justify-content-around pt-5"
        >
          {localStorage.getItem("userPersonalData") ? (
            <>
              <span className="user-avatar-large text-secondary">
                {
                  JSON.parse(localStorage.getItem("userPersonalData"))
                    .username[0]
                }
              </span>
              <h1 className="text-dark py-3 username">
                {JSON.parse(localStorage.getItem("userPersonalData")).username}
              </h1>
              <h6 className="text-secondary">
                {
                  JSON.parse(localStorage.getItem("userPersonalData"))
                    .userEmailAddress
                }
              </h6>
            </>
          ) : (
            <h1>No data found </h1>
          )}
          <Button
            variant="outline-secondary"
            className="my-3 px-5"
            onClick={() => {
              localStorage.setItem("userPersonalData", "");
              history.push("/");
            }}
          >
            Sign Out
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default UserBlogs;
